const inputDate = global.get('Timestamp'); // Get the timestamp from global context
const date = new Date(inputDate);

// Define start and end times within the day (in minutes since midnight)
const nightStartTime = 1 * 60;    // 01:00 AM converted to minutes (1 hour * 60 minutes)
const dayStartTime = 6 * 60    // 06:00 AM converted to minutes (1 hour * 60 minutes)
const peakStartTime = 17 * 60;    // 05:00 PM converted to minutes (17 hours * 60 minutes)
const peakEndTime = 21 * 60;      // 09:00 PM converted to minutes (21 hours * 60 minutes)


// Calculate the input time in minutes since midnight
let inputTime = date.getHours() * 60 + date.getMinutes();

// Check if the input time falls within the defined ranges
let timeCategory;
if (inputTime >= nightStartTime && inputTime < dayStartTime) {
    timeCategory = 'night';
} else if (inputTime >= dayStartTime && inputTime < peakStartTime) {
    timeCategory = 'day';
} else if (inputTime >= peakStartTime && inputTime < peakEndTime) {
    timeCategory = 'peak';
} else {
    timeCategory = 'evening';
}

// Set the output message payload
msg.payload = timeCategory;

// Return the message
return msg;

